import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import '../styles/RegisterPage.css'
import pocketOptionLogo from '../images/pocket option logo.png'
import ReCAPTCHA from 'react-google-recaptcha'

const RegisterPage = ({ setUser }) => {
	const [email, setEmail] = useState('')
	const [password, setPassword] = useState('')
	const [confirmPassword, setConfirmPassword] = useState('')
	const [error, setError] = useState('')
	const [isLoading, setIsLoading] = useState(false)
	const [captchaVerified, setCaptchaVerified] = useState(false)
	const navigate = useNavigate()

	const handleCaptchaChange = (value) => {
		if (value) setCaptchaVerified(true)
	}

	const handleSubmit = async (e) => {
		e.preventDefault()
		setError('')

		if (password !== confirmPassword) {
			setError('Пароли не совпадают!')
			return
		}

		try {
			setIsLoading(true)
			const newUser = {
				id: Date.now(),
				email,
				password,
			}

			const users = JSON.parse(localStorage.getItem('users')) || []
			users.push(newUser)
			localStorage.setItem('users', JSON.stringify(users))

			setUser(newUser)
			localStorage.setItem('currentUser', JSON.stringify(newUser))

			setTimeout(() => {
				navigate('/email-verification')
			}, 2000)
		} catch (err) {
			console.error('Ошибка:', err)
			setError('Произошла ошибка. Попробуйте позже.')
		} finally {
			setIsLoading(false)
		}
	}

	return (
		<div className='register-page'>
			<div className='register-content'>
				<div className='register-text'>
					<h1 className='register-title'>Создайте новый аккаунт на сайте</h1>
					<p className='register-subtext'>
						Для начала вам нужно создать аккаунт на сайте, или <br />
						же войти в уже существующий
					</p>
				</div>

				<div className='register-form-container'>
					<h2 className='register-form-title'>Регистрация</h2>
					<form onSubmit={handleSubmit}>
						<label>
							<p className='input-label'>Email</p>
							<input
								type='email'
								value={email}
								onChange={(e) => setEmail(e.target.value)}
								placeholder='example@gmail.com'
								className='register-input'
								required
							/>
						</label>
						<label>
							<p className='input-label'>Пароль</p>
							<input
								type='password'
								value={password}
								onChange={(e) => setPassword(e.target.value)}
								placeholder='A12345678a.'
								className='register-input'
								required
							/>
						</label>
						<div className='captcha-container'>
							<ReCAPTCHA
								sitekey='6LcKH5EqAAAAAH_y5gn5XEGDPB0M328pVx8X5HlW'
								onChange={handleCaptchaChange}
								theme='dark'
								hl='ru'
							/>
						</div>
						<button
							type='submit'
							className='register-button'
							disabled={isLoading}
						>
							{isLoading ? 'Создание...' : 'Зарегестрироваться'}
						</button>
					</form>
					<div className='register-result'>
						{error && <p className='register-error'>{error}</p>}
					</div>
					<p className='register-opportunity'>
						Уже есть аккаунт?{' '}
						<Link to='/login' className='login'>
							Войти
						</Link>
					</p>
				</div>
			</div>
		</div>
	)
}

export default RegisterPage
