import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../styles/PocketOptionIdPage.css'
import pocketOptionLogo from '../images/pocket option logo.png'

const PocketOptionIdPage = () => {
	const [id, setId] = useState('')
	const [error, setError] = useState('')
	const [isLoading, setIsLoading] = useState(false)
	const [success, setSuccess] = useState('')
	const navigate = useNavigate()

	const currentUser = JSON.parse(localStorage.getItem('currentUser'))

	const handleSubmit = async (e) => {
		e.preventDefault()
		setIsLoading(true)
		setError('')
		setSuccess('')

		// Simulating API Call
		setTimeout(() => {
			if (id === '12345678') {
				setSuccess('ID подтвержден!')
				navigate('/')
			} else {
				setError('ID не найден!')
			}
			setIsLoading(false)
		}, 1000)
	}

	if (!currentUser) {
		alert('Пользователь не найден. Перенаправление на главную страницу.')
		navigate('/')
		return null
	}

	return (
		<div className='pocket-option-page'>
			<div className='content-container'>
				<div className='text-container'>
					<h1 className='register-broker-title'>
						Зарегистрироваться на брокере
					</h1>
					<p className='subtext'>
						Теперь вам нужно создать аккаунт на <br />
						брокере Pocket Option
					</p>
				</div>

				<div className='verification-container'>
					<h2 className='verification-title'>
						Вам нужно создать новый аккаунт на брокере, и дать нам ваш ID
					</h2>
					<p className='verification-text'>
						Так как мы не берем плату за использование нашего сайта, мы
						пользуемся партнерской программой Брокера Pocket Option, он
						выплачивает нам 3% с вашего оборота
					</p>
					<a href='https://example.com' className='verification-button'>
						<img src={pocketOptionLogo} alt='Pocket Option' height='40px' />
					</a>
					<p className='verification-disclaimer'>
						Нажимая на кнопку выше вы автоматически переходите по нашей
						партнерской ссылке
					</p>
					<div className='id-input-container'>
						<input
							type='text'
							value={id}
							onChange={(e) => setId(e.target.value)}
							placeholder='Введите ваш ID'
							className='id-input'
						/>
						<button
							onClick={handleSubmit}
							className='verify-button'
							disabled={isLoading}
						>
							{isLoading ? 'Загрузка...' : 'Проверить'}
						</button>
					</div>
					{error && <p className='error-message'>{error}</p>}
					{success && <p className='success-message'>{success}</p>}
				</div>
			</div>
		</div>
	)
}

export default PocketOptionIdPage
