import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import '../styles/RegisterPage.css'
import pocketOptionLogo from '../images/pocket option logo.png'

const RegisterPage = ({ setUser }) => {
	const [email, setEmail] = useState('')
	const [password, setPassword] = useState('')
	const [confirmPassword, setConfirmPassword] = useState('')
	const [error, setError] = useState('')
	const [isLoading, setIsLoading] = useState(false)
	const navigate = useNavigate()

	const handleSubmit = async (e) => {
		e.preventDefault()
		setError('')

		if (password !== confirmPassword) {
			setError('Пароли не совпадают!')
			return
		}

		try {
			setIsLoading(true)
			const newUser = {
				id: Date.now(),
				email,
				password,
			}

			const users = JSON.parse(localStorage.getItem('users')) || []
			users.push(newUser)
			localStorage.setItem('users', JSON.stringify(users))

			setUser(newUser)
			localStorage.setItem('currentUser', JSON.stringify(newUser))

			setTimeout(() => {
				navigate('/email-verification')
			}, 2000)
		} catch (err) {
			console.error('Ошибка:', err)
			setError('Произошла ошибка. Попробуйте позже.')
		} finally {
			setIsLoading(false)
		}
	}

	return (
		<div className='register-page'>
			<div className='register-content'>
				<div className='register-text'>
					<h1 className='register-title'>Создайте аккаунт</h1>
					<p className='register-subtext'>
						Для использования нашего сервиса создайте аккаунт
						<br /> или войдите в существующий.
					</p>
				</div>

				<div className='register-form-container'>
					<h2 className='register-form-title'>Регистрация</h2>
					<p className='register-form-text'>
						Заполните форму ниже, чтобы создать аккаунт. Все данные будут
						защищены.
					</p>
					<form onSubmit={handleSubmit}>
						<label>
							<input
								type='email'
								value={email}
								onChange={(e) => setEmail(e.target.value)}
								placeholder='Введите email'
								className='register-input'
								required
							/>
						</label>
						<input
							type='password'
							value={password}
							onChange={(e) => setPassword(e.target.value)}
							placeholder='Введите пароль'
							className='register-input'
							required
						/>
						<button
							type='submit'
							className='register-button'
							disabled={isLoading}
						>
							{isLoading ? 'Создание...' : 'Создать аккаунт'}
						</button>
					</form>
					<div className='register-result'>
						{error && <p className='register-error'>{error}</p>}
					</div>
					<p className='register-disclaimer'>
						Уже есть аккаунт? <Link to='/login'>Войдите здесь</Link>.
					</p>
				</div>
			</div>
		</div>
	)
}

export default RegisterPage
