import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import '../styles/ProfilePage.css'

const ProfilePage = ({ user, setUser }) => {
	const navigate = useNavigate()
	const [newPassword, setNewPassword] = useState('')
	const [message, setMessage] = useState('')

	const handleLogout = () => {
		setUser(null)
		localStorage.removeItem('currentUser')
		navigate('/')
	}

	const handleChangePassword = () => {
		if (!newPassword.trim()) {
			setMessage('Пароль не может быть пустым.')
			return
		}

		const users = JSON.parse(localStorage.getItem('users')) || []
		const updatedUsers = users.map((u) =>
			u.email === user.email ? { ...u, password: newPassword } : u
		)

		localStorage.setItem('users', JSON.stringify(updatedUsers))
		const updatedCurrentUser = { ...user, password: newPassword }
		setUser(updatedCurrentUser)
		localStorage.setItem('currentUser', JSON.stringify(updatedCurrentUser))

		setMessage('Пароль успешно обновлен.')
		setNewPassword('')
	}

	if (!user) {
		return (
			<div className='profile-page'>
				<div className='profile-container'>
					<p className='login-warning'>
						Пожалуйста, войдите в аккаунт, чтобы просмотреть профиль.
					</p>
				</div>
			</div>
		)
	}

	return (
		<div className='profile-page'>
			<div className='profile-container'>
				<h1 className='profile-title'>Настройки аккаунта</h1>
				<div className='profile-block'>
					<p className='input-label'>Email</p>
					<div className='profile-info'>{user.email}</div>
				</div>
				<div className='profile-block'>
					<p className='input-label'>Пароль</p>
					<input
						type='password'
						value={newPassword}
						onChange={(e) => setNewPassword(e.target.value)}
						placeholder='Введите новый пароль'
						className='profile-input'
					/>
					<button
						className='change-password-button'
						onClick={handleChangePassword}
					>
						Сохранить новый пароль
					</button>
					{message && <p className='success-message'>{message}</p>}
					<p className='disclaimer'>
						*Для изменения пароля, введите код отправленный на вашу почту
					</p>
				</div>
				<button className='logout-button' onClick={handleLogout}>
					Выйти
				</button>
			</div>
		</div>
	)
}

export default ProfilePage
